import React from "react";
import {
  StyleSheet,
  TouchableHighlightBase,
  Pressable,
  Text,
} from "react-native";

export default function CustomButton({ text, handlePress }) {
  return (
    <TouchableHighlightBase style={styles.container} onPress={handlePress}>
      <Text style={styles.text}>{text}</Text>
    </TouchableHighlightBase>
  );
}

const styles = StyleSheet.create({
  container: {
    width: "90%",
    height: 40,

    alignItems: "center",
    justifyContent: "center",

    marginTop: 30,

    borderRadius: 5,

    backgroundColor: "red",
  },
  text: {
    fontSize: 16,
  },
});
